import React, { Component, useEffect, useState } from "react";
import {
    useParams,
    useNavigate,
    useLocation,
    Link,
} from "react-router-dom";
import { useAppDispatch, useAppSelector } from "../app/hooks";
import MessageListPage from "../pages/MessagePage";
import { deleteMessage } from "./messageApi";
import "./MessagePageStyle.css"
import { apiGetAllMessage, Message_, selectMessage } from "./messageSlice";
export default function Message(this: any, props:any){
    const message_:Message_ = props.message;
    const dispatch = useAppDispatch();
    let navigate = useNavigate();
    let {Username} = useParams();
    useEffect(()=>{
        dispatch(apiGetAllMessage());
    },[]);
    const messages = useAppSelector(selectMessage);
    let BtnDeleteCick=()=>{
        console.log("BtnDelete")
    deleteMessage(message_)
    return messages
}
    console.log(message_) 
    class Message_UI extends React.Component{
        state={message :message_}
        render(){
            return(<div><div>
                <div className={"message_div"}>
                    <h4 className={"MessageName"}>{message_.username}</h4>
                    <h6 className={"MessageFood"}>Food : {message_.FoodName}</h6>
                    <h6 className={"Messagecount"}>Count : {message_.count}</h6>
                    <h6 className={"date__"}>date : {message_.date}</h6>
                    <button className={"OpenBtn"} onClick={()=>{
                        navigate(`/${Username}/message/detail/${message_._id}`)
                    }}>Open</button>
                    <button className={"DeleteBtn"} onClick={()=>{
                       let messages = BtnDeleteCick()
                       this.setState({message:""})
                        this.setState({message:messages})
                    }}>Delete</button>
                </div>
                </div></div>)
        }
    }
    return(<div>
        <Message_UI/>
    </div>)
}
