import { useParams } from "react-router-dom";
import {useAppDispatch, useAppSelector} from "../app/hooks";
import {Movie, selectMovieById} from "../features/movie/movieSlice";
import app from "../App"
import "./DetailStyle.css"
import {
   selectFoodById
} from "../Shop/ShopSlice";
import ReviewUI from "../features/review/ReviewUI";
import {useEffect} from "react";
import ReviewListUI from "../features/review/ReviewListUI";
import { Link, useNavigate } from "react-router-dom";
import DownloadPage from "./DownloadPage";
export default function FoodDetailsPage() {
    let {foodId} = useParams();
    let navigate = useNavigate()
    var food = useAppSelector(state=>selectFoodById(state,foodId!));
    console.log("Movie ",food);
    const DownloadClick = ()=>{
        navigate("/download")
    }
    return (
        <div className={"Main__"} style={{backgroundImage: `url("${food.FoodImage}")`}}>
            <div className={"Detail_"} style={{backgroundColor: `${food.ColorForStyle}`}}>
                    <h2 className={"detail_title"}>{food.Food_name}</h2>
                    <h4 className={"detail_year"}> expire_date: {food.expire_date}</h4>
                    <div className={"summary_div"}>
                        <h6 className={'summary'}>{food.Ingredients}</h6>
                    </div>
                    <button className={"Download"} onClick={()=>{
                        DownloadClick()
                    }}>Download</button>
            </div>
    </div>)

}
