import React from "react"
import "./UserSettingStyle.css"
export default function UserSetting(props:any){
    let Username = props.username
    class SettingProfile extends React.Component {
        state = {
            textDisplay : "",
            inputDisplay: "none"
        }
        render(){
            return(
                <div className="UserSetting_Div">
            <h6 className="Username_InSetting" style={{display:this.state.textDisplay}}>Username:{Username}</h6>
            <label className="UsernameInputInProfile" style={{display:this.state.inputDisplay}}>Username:</label>
            <input value={Username} className={"UserNameInputInProfile"} style={{display:this.state.inputDisplay}}/>
            <i className='far fa-edit' style={{fontSize:"20px",marginRight:"500px"}} onClick={()=>{
                this.setState({
                    textDisplay:"none",
                    inputDisplay:""
                })
            }}></i>
            </div>
            )
    }
    }
    return(
        <SettingProfile/>
    )
}
