import { Component } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { useAppDispatch, useAppSelector } from "../app/hooks";
import React, {useEffect, useState} from "react";
import { apiGetAllFood, Food, selectFood } from "../Shop/ShopSlice";
import { render } from "@testing-library/react";
import FoodUI from "../Shop/FoodShopUI";
import "./ShopPageStyle.css"
import { DeleteFood } from "../Shop/ShopApi";

export default function Shop_Page(){
    const dispatch = useAppDispatch();
    let navigate = useNavigate();
    let Foods_list: Food[]=[]
    let {Username} = useParams();
    let home = `/${Username}/home`;
    let movie_list = `/${Username}/movie-list`
    let profile = `/${Username}/profile`
    let shop = `/${Username}/shop`
    let Title ="Admin"
    let widths = "120px"
    let margin = "0px"
    useEffect(()=>{
       dispatch(apiGetAllFood());
    },[]);
    if (Username != "Aung Nyi Thant"){
        Title="BILLIONER"
        widths ="146px"
        margin = "6px"
    }
    var today = new Date();
    var date = today.getFullYear()+'-'+(today.getMonth()+1)+'-'+today.getDate();
    console.log(date)
    var Foods = useAppSelector(selectFood);
    for (let i=0;i<Foods.length;i++){
        let year = Foods[i].expire_date.slice(0,4).replace("-","")
        let moth = Foods[i].expire_date.slice(5,7).replace("-","")
        let days = Foods[i].expire_date.slice(8,10).replace("-","")
        let nowYear= date.slice(0,4).replace("-","")
        let nowMoth = date.slice(5,7).replace("-","")
        let nowDays = date.slice(8,10).replace("-","")
        let id = Foods[i]._id
        if(Number(nowYear) > Number(year)){
            DeleteFood(Foods[i]._id)
    }
    let clickCount = 0
    let met: Food[] =[]
    let Cooked =[]
class Shop_Page_ extends Component{

    // handleClick=()=>{
    //     console.log(this.state.Food)
    // };

        state={
            Food:Foods,
            text:"Admin"
        }
    serchClick=()=>{
        this.setState({Food:Foods_list})
    }
    handleClick=()=>{
        this.setState({Food:"Color"})
    }
    MeatClick=()=>{
        this.setState({Foods:[]})
        this.setState({Foods:met})
    }
    render(){
        return(
            <div>
                <meta name="viewport" content="width=device-width, initial-scale=1"></meta>
                <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0/css/all.min.css" integrity="sha512-9usAa10IRO0HhonpyAIVpjrylPvoDwiPUiKdWk5t3PyolY1cOd4DSE0Ga+ri4AuTroPR5aQvXU9xC6qOPnzFeg==" crossOrigin="anonymous" referrerPolicy="no-referrer" />
<meta name='viewport' content='width=device-width, initial-scale=1'></meta>
<script src='https://kit.fontawesome.com/a076d05399.js' crossOrigin='anonymous'></script>
<link rel="stylesheet" href="https://www.w3schools.com/w3css/4/w3.css"></link>
<link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css"></link>
          <nav className="navbar navbar-expand-lg navbar-light bg-light">
          <link rel="stylesheet" href="https://fonts.googleapis.com/icon?family=Material+Icons"></link>
<div className="collapse navbar-collapse" id="navbarSupportedContent">
        <ul className="navbar-nav mr-auto">
            <div className={"home"}>
        <li className="nav-item active">
            <div >
            <li className="w3-xxlarge">
                <i className="fa fa-home"></i></li>
                </div>
            </li>
            </div>
            <li className="nav-item active">
            <li className="nav-item active">
            <div className={"ProfileIcon"}>
            <li className="w3-xxlarge">
                <i className="material-icons w3-xxlarge" onClick={()=>{
                    navigate(movie_list)
                }}>menu</i></li>
                </div>
            </li>
            </li>
            <li className="nav-item active">
            <li className="nav-item active">
            <div className={"ProfileIcon"} onClick={()=>{ navigate(shop)}}>
            <li>
            <i className='fa-solid fa-shop'></i></li>
                </div>
            </li>
            </li>
            <div className={"Admin_title"} style={{width:widths, boxShadow:"0 10px 10px 0 rgba(0, 0, 0, 0.2)"}} onClick={()=>{
                if(Username == "Aung Nyi Thant"){
                    navigate(`/admin/${Username}/shop`)
                }
            }}>
            <h3 className={"Title__"} id={"title"} style={{marginLeft:margin}} >{Title}</h3>
            </div>
            {/* <div className={"Message"} onClick={()=>{
                navigate("/Aung%20Nyi%20Thant/message")
            }}><h4>M</h4></div> */}
            <i className='fas fa-comment-alt' style={{fontSize:"35px" , marginTop:"10px"}} onClick={()=>{
                navigate(`/${Username}/message`)
            }}></i>

            {/*{
            useAuthentication()
            && <li className="nav-item">
                <Link className="nav-link" to="/movie-list">Movie List</Link>
            </li>
        }

        {useAuthentication() && <li className="nav-item">
            <Link className="nav-link" to="/logout">Logout</Link>
        </li>}*/}


        </ul>

    </div>
    </nav>
    <div className="input-group">
    <div className="form-outline">
  <input type="search" id="form1" className="form-control" placeholder="Taste (or) Name"  aria-label="Search" style={{marginTop:"20px"}}/>
</div>
    <button type="button" className="btn btn-outline-primary" style={{marginTop:"20px"}} onClick={()=>{
        var input = (document.getElementById("form1") as HTMLInputElement).value;
        if(input != ""){
        for (let i = 0; i < Foods.length; i++) { 
            let taste = Foods[i].typeOfTaste
            let name_ = Foods[i].Food_name.toLowerCase()
            if(taste == input){
                Foods_list.push(Foods[i])
            }else if(name_ == input){
                Foods_list.push(Foods[i])
            }
        }
        if(clickCount == 1){
            window.location.reload()
        }else{
            this.setState({Food:[]})
            console.log(clickCount)
            this.serchClick()
            clickCount= clickCount+1
        }
    }}}>search</button>
    {/* <div className={"animation"}>
        <div className={"child"} id={"loading"}></div>
    </div> */}
    </div>
            {
                this.state.Food.map((food:Food)=>
                    <FoodUI
                        food={food}
                        />
                )
            }
            </div>
        )
    }
}
return(
    <Shop_Page_/>
)
}}
