    }

    return (
            <div className={"Movie__"}>
                <nav className="navbar navbar-expand-lg navbar-light bg-light">


<div className="collapse navbar-collapse" id="navbarSupportedContent">
        <ul className="navbar-nav mr-auto">
            <li className="nav-item active">
                <Link className="nav-link" to="/home">Home</Link>
            </li>
            <li className="nav-item active">
                <Link className="nav-link" to="/movie-list">Movie list</Link>
            </li>
            <li className="nav-item active">
                <Link className="nav-link" to="/profile">Profile</Link>
            </li>

            {/*{
            useAuthentication()
            && <li className="nav-item">
                <Link className="nav-link" to="/movie-list">Movie List</Link>
            </li>
        }

        {useAuthentication() && <li className="nav-item">
            <Link className="nav-link" to="/logout">Logout</Link>
        </li>}*/}


        </ul>

    </div>
    </nav>
    <div className="input-group">
    <div className="form-outline">
  <input type="search" id="form1" className="form-control" placeholder="Type query"  aria-label="Search" />
</div>
    <button type="button" className="btn btn-outline-primary" onClick={()=>{
        var input = (document.getElementById("form1") as HTMLInputElement).value;
        for (let i = 0; i < Foods.length; i++) { 
            let name = Foods[i].Food_name.toLowerCase()
            if(input== name){
                navigate(`/shop/food-details/${Foods[i]._id}`)
            }
        }
    }}>search</button>
    </div>

            {
                Foods.map((food:Food)=>
                    <FoodUI
                        food={food}/>
                )
            }
            </div>
    );
}
