import { useEffect } from "react";
export default function SignPage(){
    const dispatch = useAppDispatch();
    useEffect(()=>{
        dispatch(apiGetAllUsers());
    },[]);

    let users = useAppSelector(selectUser);
    let navigate = useNavigate();
    const SignClick = () =>{
        let isNull = "Y"
