        var users1:User1={
            userName:Username,
            password:password,
            email:Email
        }
        if (FirstName!=""){
            if(LatestName!=""){
                if(Age != ""){
                    if(password!=""){
                        if(password==RePassword){
                            for (let i =0;i<users.length;i++){
