import MovieList from "../features/movie/MovieList";
import {useAppDispatch, useAppSelector} from "../app/hooks";
import {
    apiGetAllUsers,
    apiSaveUsers,
    Users,
    selectUser
} from "../features/Login/UsersSlice";
import "./MovieListPagestyle.css"
import React, {useEffect, useState} from "react";
import {Modal} from "react-bootstrap";
import {Form, Formik} from "formik";
import * as Yup from 'yup';
import Swal from 'sweetalert2';

import {useDispatch} from "react-redux";

import {saveMovie, updateMovie} from "../features/movie/movieApi";
import LoginPage from "../features/Login/LoginUi";
import { Link } from "react-router-dom";

const UsersForm = (props:any)=>{
    let dispatch = useAppDispatch();
    let users = props.movie;
    console.log('Edit movie ',users);
    let handleClose = props.handleClose;
    let initValues ={
        _id : users? users._id:'',
        username: users? users.username:'',
        password: users? users.password:'',
    }
    return (<div>

        <Formik
            initialValues={initValues}
            onSubmit={values => {
                // same shape as initial values
                //console.log("Handle Close ", handleClose);
                console.log(values);

                if (!users)//save
                {
                    let newUsers: Users = {
                        // title: values.title,
                        // year: Number(values.year),
                        // image_adress:values.image_adress,
                        // background_img:values.background_img,
                        // color:values.color,
                        // summary:values.summary
                        username: values.username,
                        password: values.password,
                    }

                    console.log('Movie ', newUsers);
                    dispatch(apiSaveUsers(newUsers));
                }
                handleClose();
            }}
        >
        </Formik>

    </div>);
};
export default function MovieListPage() {
    const dispatch = useAppDispatch();
    useEffect(()=>{
        dispatch(apiGetAllUsers());
    },[]);

    const movies = useAppSelector(selectUser);

    const [editMovie,setEditMovie] = useState(null);
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const btnAddNewMovieClick = ()=>{
        console.log('Btn Add New Movie click');
        setEditMovie(null);
        handleShow();
    };

    const btnEditClick=(movie:any)=>{
        console.log('Btn Edit Movie click ',movie);
        setEditMovie(movie);
        handleShow();
    }
    const btnDeleteClick = (movie:any)=>{
      console.log('Delete click ',movie);
        Swal.fire({
            title: 'Do you want to delete movie?',
            showCancelButton: true,
            confirmButtonText: 'Delete',

        }).then((result) => {
            /* Read more about isConfirmed, isDenied below */
            if (result.isConfirmed) {
                dispatch(apiDeleteMovie(movie)).then(()=>{
                    Swal.fire('Deleted!', '', 'success');
                });

            }
        })
    };
    return (
        <div>
                <nav className="navbar navbar-expand-lg navbar-light bg-light">


    <div className="collapse navbar-collapse" id="navbarSupportedContent">
            <ul className="navbar-nav mr-auto">
                <li className="nav-item active">
                    <Link className="nav-link" to="/home">Home</Link>
                </li>
                <li className="nav-item active">
                    <Link className="nav-link" to="/movie-list">Movie list</Link>
                </li>
                <li className="nav-item active">
                    <Link className="nav-link" to="/profile">Profile</Link>
                </li>

                {/*{
                useAuthentication()
                && <li className="nav-item">
                    <Link className="nav-link" to="/movie-list">Movie List</Link>
                </li>
            }

            {useAuthentication() && <li className="nav-item">
                <Link className="nav-link" to="/logout">Logout</Link>
            </li>}*/}


            </ul>

        </div>
        </nav>
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>{editMovie?'Update Movie':'New Movie'}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                   <MovieForm handleClose={handleClose}
                              movie ={editMovie}
                             />

                </Modal.Body>
                {/*<Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={handleClose}>
                        Save Changes
                    </Button>
                </Modal.Footer>*/}
            </Modal>
            <div className={"Movie__"}>
            {
                movies.map((movie:Movie)=>
                    <MovieUI
                        key={movie._id}
                        editClick = {btnEditClick}
                        deleteClick = {btnDeleteClick}
                        movie={movie}/>
                )
            }
            </div>
        </div>
    );
}
