import { createAsyncThunk, createSlice, PayloadAction } from '@reduxjs/toolkit';
import { RootState, AppThunk } from '../app/store';
import {ToDo, ToDoList} from "../features/todos/todoSlice";
import {getAllFood} from "./ShopApi";

export interface Food {
    _id? : string,
    Food_name: string,
    prices: string,
    expire_date:string,
    FoodImage:string,
    typeOfTaste:string,
    age_permisssion:string,
    Brand:string,
    Ingredients:string,
    Rating:string,
    textStyle:string,
    country:string,
    ColorForStyle:string
};
export interface MovieList {
    movies: Array<Food>
}

const initialState: MovieList = {
    movies : [
        {
            _id : "1",
            Food_name:"",
            prices:"",
            expire_date:"",
            FoodImage:"",
            typeOfTaste:"",
            age_permisssion:"",
            Brand:"",
            Ingredients:"",
            Rating:"",
            textStyle:"",
            country:"",
            ColorForStyle:""
        }
    ]
};
export const apiGetAllFood = createAsyncThunk(
    'movie/getAllMovie',
    async () => {
        console.log("API get all foods");
        const response = await getAllFood();

        console.log("All movie json ",response.data);
        return response.data;
    }
);
export const movieSlice = createSlice({
    name: 'movie',
    initialState,
    reducers: {
        addMovie: (state, action: PayloadAction<ToDo>) => {
            //state.items = [...state.items, action.payload];
        },

    },
    extraReducers: (builder) => {
        builder
            .addCase(apiGetAllFood.fulfilled, (state, action) => {
                console.log("Api fullfilled ", action.payload);
                state.movies = action.payload;

            })
        }
    }
);

export const { addMovie } = movieSlice.actions;
export const selectMovie = (state: RootState) => state.movie.movies;
export const selectMovieById =  (state: RootState,movieId:string) => state.movie.movies.filter(movie=>movie._id ==movieId)[0];
export default movieSlice.reducer;
