import { useParams } from "react-router-dom";
import {useAppDispatch, useAppSelector} from "../app/hooks";
import {Movie, selectMovieById} from "../features/movie/movieSlice";
import app from "../App"
import "./FoodStyle.css"
import {
    Food,
    getFooById,
   selectFoodById,
} from "../Shop/ShopSlice";
import ReviewUI from "../features/review/ReviewUI";
import {useEffect} from "react";
import ReviewListUI from "../features/review/ReviewListUI";
import { Link, useNavigate } from "react-router-dom";
import DownloadPage from "./DownloadPage";
export default function FoodDetailsPage() {
    let dispatch = useAppDispatch();
    let {foodID} = useParams();
    let navigate = useNavigate()
    let food = useAppSelector(state=>selectFoodById(state,foodID!));
    console.log(foodID)
    console.log("Food ",food);
    const DownloadClick = ()=>{
        navigate("/download")
    }
    return (
        <div className={"main_profile"}>
            <nav className="navbar navbar-expand-lg navbar-light bg-light">


    <div className="collapse navbar-collapse" id="navbarSupportedContent">
        <ul className="navbar-nav mr-auto">
            <li className="nav-item active">
                <Link className="nav-link" to="/home">Home</Link>
            </li>
            <li className="nav-item active">
                <Link className="nav-link" to="/movie-list">Movie list</Link>
            </li>
            <li className="nav-item active">
                <Link className="nav-link" to="/profile">Profile</Link>
            </li>

            {/*{
            useAuthentication()
            && <li className="nav-item">
                <Link className="nav-link" to="/movie-list">Movie List</Link>
            </li>
        }

        {useAuthentication() && <li className="nav-item">
            <Link className="nav-link" to="/logout">Logout</Link>
        </li>}*/}


        </ul>

    </div>
    </nav>
        <div className={"profile_photo"} style={{backgroundImage:`url("${food.FoodImage}")`}}></div>
        <div>
        <div className={"Ifo"}>
            <h3 className={"Text_title"} style={{color:`${food.ColorForStyle}`}}> {food.Food_name}</h3>
            <h4 className={"Text"} style={{color:`${food.ColorForStyle}`}}>{food.Prices}</h4>
            <div className="all_Info" style={{backgroundColor:`${food.ColorForStyle}`}}>
                <div className={"Ifo_textName_Div"}>
                <p className={"Bio_text"}>{food.Ingredients}</p>
                </div>
                <h6 className={"Ifo_text"}>Age:{food.age_permisssion}</h6>
                <h6 className={"Ifo_text"}>made in {food.country}</h6>
                <h6 className={"Ifo_text"}>{food.Brand}</h6>
                <h6 className={"Ifo_textName"}>Expire Date:{food.expire_date}</h6>
            </div>
        </div>
        </div>
    </div>
        )

}

