import {DeleteFoods, Food, Foods, FoodUpdate} from "./ShopSlice";
import "./Food.css";
import {useNavigate, useParams} from "react-router-dom";
import React, {useEffect} from "react";
import { saveFood, UpdateFood } from "./ShopApi";
import { useAppDispatch, useAppSelector } from "../app/hooks";
import { apiGetAllMessage ,Message_ , selectMessage } from "../message/messageSlice";
import { saveMessage } from "../message/messageApi";
export default function FoodUI (props:any) {

    let navigate = useNavigate();
    var today = new Date();
    var date = today.getFullYear()+'-'+(today.getMonth()+1)+'-'+today.getDate();
    let food:Food = props.food;
    const dispatch = useAppDispatch();
    let clickCount = 0
    let likeFood = 0
    let nowYear= date.slice(0,4).replace("-","")
    let year = food.expire_date.slice(0,4).replace("-","")
    let count = 0
    let age_perm: string | number | boolean | React.ReactElement<any, string | React.JSXElementConstructor<any>> | React.ReactFragment | null | undefined ;
    if (food.age_permisssion == "&"){
        age_perm= "NoLimit"
    }else{
        age_perm=food.age_permisssion
    }
    let currentData = new Date(date);
    let date_ = new Date(food.expire_date);
    if (currentData > date_){
        DeleteFoods(food)
    }
    useEffect(()=>{
        dispatch(apiGetAllMessage())
     },[]);
     var message_ = useAppSelector(selectMessage)
    const btnDetailHandler=()=>{
        console.log('Go to detail');

        navigate(`/${Username}/shop/food-details/${food._id}`);
    };
    let Message_ : Message_ = {
        username:"Aung Nyi Thant",
        FoodName:"message",
        Message:`This food is expire Foodid ${food._id},Food name is ${food.Food_name}`,
        count:"message",
        cost:"message",
        date:date

    }
    if(Number(nowYear) > Number(year)){
        for (let i =0;i<message_.length;i++ ){
            if(message_[i].Message == Message_.Message){
                count = 1
                break
            }else{

            }
        }
        if(count = 0){
            saveMessage(Message_)
        }else{
            //nothing
        }
    }
    let {Username} = useParams();
    console.log("Food is",food)
    let width = window.innerWidth
    let img_width = "200px"
    let margin = "60px"
    console.log(width)
    if (width <= 500){
        img_width="100px"  
        margin="20px"
    }
    class FoodPage extends React.Component{
        state={Color:"",
            food:food,
            like:""}
            like = this.state.food.like
        render(){
            return(<div className={"MainFood"}>
            <div className={'food_'} style={{backgroundColor: `#4aa55e`,marginLeft:margin}}>
            <div onClick={()=>{btnDetailHandler()}}>
                <img src={food.FoodImage} className={"img_food"} id = {"img"} onClick={()=>{navigate(`/movie-details/${food._id}`);}} style={{width:img_width,height:img_width}}/>
            </div>
            <div className={'food-title'}>
                {/* <iframe className="responsive-iframe" src="https://www.youtube.com/embed/tgbNymZ7vqY"></iframe> */}
                <h3>
                 { food.Food_name}
                 </h3>
                 <i className="fa fa-heart" style={{fontSize:"15px" , float:"left", marginTop:"33px" , marginLeft:"15px" , color:this.state.Color }} onClick={()=>{
                     clickCount = clickCount+1
                     let count = clickCount%2
                     if (clickCount == 1){
                        this.setState({Color:"red"})
                        console.log(this.like)
                        this.like=this.like+1
                        let likeString = this.like.toString()
                        let Food_ : Food = {
                            _id:this.state.food._id,
                            Food_name:this.state.food.Food_name,
                            FoodImage:this.state.food.FoodImage,
                            expire_date:this.state.food.expire_date,
                            typeOfTaste:this.state.food.typeOfTaste,
                            Prices:this.state.food.Prices,
                            age_permisssion:this.state.food.age_permisssion,
                            Brand:this.state.food.Brand,
                            Ingredients:"SomeThing",
                            Rating:"0.0",
                            textStyle:"Something",
                            ColorForStyle:"#4aa55e",
                            country:this.state.food.country,
                            like:this.like}
                            UpdateFood(Food_)
                            this.setState({like:likeString})
                     }else if(count === 0){
                         this.setState({Color:""})
                         this.like = this.like-1
                         let likeString = this.like.toString()
                          let Food_ : Food = {
                            Food_name:this.state.food.Food_name,
                            FoodImage:this.state.food.FoodImage,
                            expire_date:this.state.food.expire_date,
                            typeOfTaste:this.state.food.typeOfTaste,
                            Prices:this.state.food.Prices,
                            age_permisssion:this.state.food.age_permisssion,
                            Brand:this.state.food.Brand,
                            Ingredients:"SomeThing",
                            Rating:"0.0",
                            textStyle:"Something",
                            ColorForStyle:"#4aa55e",
                            country:this.state.food.country,
                            like:this.like}
                            FoodUpdate(Food_)
                            this.setState({like:""})
                     }else{
                         this.setState({Color:"red"})
                         this.like = this.like+1
                         let likeString = this.like.toString()
                          let Food_ : Food = {
                            Food_name:this.state.food.Food_name,
                            FoodImage:this.state.food.FoodImage,
                            expire_date:this.state.food.expire_date,
                            typeOfTaste:this.state.food.typeOfTaste,
                            Prices:this.state.food.Prices,
                            age_permisssion:this.state.food.age_permisssion,
                            Brand:this.state.food.Brand,
                            Ingredients:"SomeThing",
                            Rating:"0.0",
                            textStyle:"Something",
                            ColorForStyle:"#4aa55e",
                            country:this.state.food.country,
                            like:this.like}
                            FoodUpdate(Food_)
                            this.setState({like:likeString})
                     }
                     console.log(likeFood)
                 }}>{this.state.like}</i>
            </div>
            <div className={"YearsDiv"}>
                <h5 className={"Year"}>Age : {age_perm}</h5> &nbsp;
            </div>

        </div>
        </div>)
        }
    }
    return ((<FoodPage/>))}
