import {Movie} from "./movieSlice";
import "./Movie.css";
import {Link, useNavigate} from "react-router-dom";
import React, {useEffect} from "react";
export default function MovieUI(props:any) {

    let navigate = useNavigate();

    const movie:Movie = props.movie;
    const btnEditClickHandler=()=>{
        props.editClick(movie);
    };
    const btnDetailHandler=()=>{
        console.log('Go to detail');

        navigate(`/movie-details/${movie._id}`);
    };
    return (<div className={"Mainmovie"}>
            <nav className="navbar navbar-expand-lg navbar-light bg-light">


    <div className="collapse navbar-collapse" id="navbarSupportedContent">
        <ul className="navbar-nav mr-auto">
            <li className="nav-item active">
                <Link className="nav-link" to="/home">Home</Link>
            </li>
            <li className="nav-item active">
                <Link className="nav-link" to="/movie-list">Movie list</Link>
            </li>
            <li className="nav-item active">
                <Link className="nav-link" to="/profile">Profile</Link>
            </li>

            {/*{
            useAuthentication()
            && <li className="nav-item">
                <Link className="nav-link" to="/movie-list">Movie List</Link>
            </li>
        }

        {useAuthentication() && <li className="nav-item">
            <Link className="nav-link" to="/logout">Logout</Link>
        </li>}*/}


        </ul>

    </div>
    </nav>
        <div className={'movie'}>
        <div>
            <img src={movie.image_adress} className={"img_movie"}/>
        </div>
        <div className={'movie-title'}>
            <h3>
             { movie.title}
             </h3>
        </div>
        <div>
            <h5 className={"Years"}>Year {movie.year}</h5> &nbsp;
            <button type={"button"}
                className={"btn btn-primary"}
                onClick={btnEditClickHandler}>
                Edit
            </button>
            &nbsp;
            &nbsp;
            <button type={"button"}
                    className={"btn btn-primary"}
                    onClick={()=>props.deleteClick(movie)}>
                Delete
            </button>
            &nbsp;
            &nbsp;
            <button type={"button"}
                    className={"btn btn-primary"}
                    onClick={btnDetailHandler}>
                Details
            </button>
        </div>

    </div>
    </div>)
};
