import { Component } from "react";
import { useNavigate } from "react-router-dom";
import { useAppDispatch, useAppSelector } from "../app/hooks";
import React, {useEffect, useState} from "react";
import { apiGetAllFood, Food, selectFood } from "../Shop/ShopSlice";
import { render } from "@testing-library/react";
import FoodUI from "../Shop/FoodShopUI";
export default function Shop_Page(){
    const dispatch = useAppDispatch();
    const [state, setState]= useState(0)
    let Foods_list: Food[]=[]
    useEffect(()=>{
       dispatch(apiGetAllFood());
    },[state]);
    var Foods = useAppSelector(selectFood);
class Shop_Page_ extends Component{

    // handleClick=()=>{
    //     console.log(this.state.Food)
    // };

        state={
            Food:Foods
        }
    serchClick=()=>{
        this.setState({Food:[]})
        this.setState({Food:Foods_list})
    }
    handleClick=()=>{
        this.setState({Food:"Color"})
    }
    render(){
        return(
            <div>
                <div className="form-outline">
  <input type="search" id="form1" className="form-control" placeholder="Serch Taste"  aria-label="Search"/>
</div>
    <button type="button" className="btn btn-outline-primary" onClick={()=>{
        var input = (document.getElementById("form1") as HTMLInputElement).value;
        for (let i = 0; i < Foods.length; i++) { 
            let taste = Foods[i].typeOfTaste
            if(taste == input){
                Foods_list.push(Foods[i])
            }
        }
        this.serchClick
    }}>search</button>
            {
                this.state.Food.map((food:Food)=>
                    <FoodUI
                        food={food}
                        />
                )
            }
            </div>
        )
    }
}
return(
    <Shop_Page_/>
)
}
