import {
    useParams,
    useNavigate,
    useLocation,
    Link,
} from "react-router-dom";
import MessageListPage from "../pages/MessagePage";
import { deleteMessage } from "./messageApi";
import "./MessagePageStyle.css"
import { Message_ } from "./messageSlice";
export default function Message(props:any){
    const message_:Message_ = props.message;
    let navigate = useNavigate();
    let {Username} = useParams();
    console.log(message_) 
    const [message, setCount]=useState(message_)
    return(
        <div>
    <div className={"message_div"}>
        <h4 className={"MessageName"}>{message.username}</h4>
        <h6 className={"MessageFood"}>Food : {message.FoodName}</h6>
        <h6 className={"Messagecount"}>count : {message.count}</h6>
        <button className={"OpenBtn"}>Open</button>
        <button className={"DeleteBtn"} onClick={()=>{
            deleteMessage(message_)
            navigate(`/${Username}/adminShop`)
        }}>Delete</button>
    </div>
    </div>
    )
}
