import { Form, Formik } from "formik";
import React, { useEffect } from "react";
import { useParams } from "react-router"
import { useAppDispatch, useAppSelector } from "../app/hooks";
import UsersSlice, { selectUserById, Users } from "../features/Login/UsersSlice";
import { apiGetAllMessage,saveMessage, apiSaveMessage, Message_, selectMessage } from "../message/messageSlice";
import * as Yup from 'yup';

const Messagechema = Yup.object().shape({
    title: Yup.string()
        .min(2, 'Too Short!')
        .max(50, 'Too Long!')
        .required('Required'),

    year: Yup.number().required('Required'),
});
const MessageForm = (props:any)=>{
    let dispatch = useAppDispatch();
    let message = props.message;
    console.log('Edit movie ',message);
    let handleClose = props.handleClose;
    let initValues ={
        _id : message? message._id:'',
        username:  message? message.username:'',
        FoodName:  message? message.FoodName:'',
        count: message? message.count:'',
        cost:message? message.cost:'',
        date:message? message.date:"",
        Message:message? message.Message:''
    }
    return (<div>

        <Formik
            initialValues={initValues}
            validationSchema={Messagechema}
            onSubmit={values => {
                // same shape as initial values
                //console.log("Handle Close ", handleClose);
                console.log(values);

                if (!message)//save
                {
                    let newMessage: Message_ = {
                        username:values.username,
                        FoodName:values.FoodName,
                        count:values.count,
                        cost:values.cost,
                        date:values.date,
                        Message:values.Message
                    }

                    console.log('Movie ', newMessage);
                    dispatch(apiSaveMessage(newMessage));
                }
                else //Update movie
                {
                    let messageToUpdate: Message_ = {
                        _id : values._id,
                        username:values.username,
                        FoodName:values.FoodName,
                        count:values.count,
                        cost:values.cost,
                        date:values.date,
                        Message:values.Message
                    }

                }

                handleClose();
            }}
        >
            {({
                  values,
                  errors,
                  touched,
                  handleChange,
                  handleBlur,
                  handleSubmit,
                  isSubmitting
              }) => (
                <Form>
                    <label>Title</label>
                    <input type="text"
                           className="form-control"
                           id="title"
                           name="title"
                           onChange={handleChange}
                           onBlur={handleBlur}
                           value={values.username}
                    />
                    {errors.username && touched.username ? (
                        <div className="alert alert-danger">{errors.username.toString()}</div>
                    ) : null}

                    <label>Year</label>
                    <input type="text"
                           className="form-control"
                           id="year"
                           name="year"
                           onChange={handleChange}
                           onBlur={handleBlur}
                           value={values.username}
                    />
                    {errors.username && touched.username ? <div className="alert alert-danger">{errors.username.toString()}</div> : null}
                    <button type="submit"
                            className="btn btn-primary">{message?'Update':'Save'}
                    </button>
                    <button type="button"
                            onClick={()=>handleClose()}
                            className="btn">Cancel
                    </button>
                </Form>
            )}
        </Formik>

    </div>);
};
export default function(){
    useEffect(()=>{
        dispatch(apiGetAllMessage());
    },[]);
    const messages = useAppSelector(selectMessage);
    let {ID_} = useParams()
    let {Username} = useParams()
    let user:Users = useAppSelector(state=>selectUserById(state,ID_!));
    const dispatch = useAppDispatch();
    const btnClick = ()=>{
        let date_ = new Date().toISOString().slice(0, 10)
        if (Username != undefined){
        let message:Message_ = {
            username:Username,
            cost: "",
            count:"",
            FoodName:"",
            date:date_,
            Message:""
        }
    }
        saveMessage(message)
    }
    console.log(user)
    return(
        <div><h3>{user.username}</h3></div>

    )
}
